var pipi_localStorage=function(){};pipi_localStorage.prototype.add=function(a,b){return null==localStorage.getItem(a)?(localStorage.setItem(a,b),{success:!0,message:"ok"}):{success:!1,message:"cookie name repeat"}};pipi_localStorage.prototype.botain=function(a){return null==localStorage.getItem(a)?{success:!1,message:"Not found"}:{success:!0,message:localStorage.getItem(a)}};pipi_localStorage.prototype.revise=function(a,b){if(null==localStorage.getItem(a))return{success:!1,message:"Name does not exist"};localStorage.setItem(a,b);return{success:!0,message:"ok"}};pipi_localStorage.prototype["delete"]=function(a){if(null==localStorage.getItem(a))return{success:!1,message:"Name does not exist"};localStorage.removeItem(a);return{success:!0,message:"ok"}};